import React from 'react';

class TodoItems extends React.Component {
    createTasks = item => {
        return (
            <li key={item.key}>

                <input type="checkbox"
                    value={item.text}
                    checked={this.props.checked}
                    onChange={this.props.checkItem}
                />

                {/* <label text={item.text}>
                    <input type="checkbox" />
                    onClick={() => this.props.checkItem(item.key)} />
                </label> */}

            </li>
        )
    }

    render() {
        const todoEntries = this.props.entries;
        const listItems = todoEntries.map(this.createTasks);
        return <ul className="theList">{listItems}</ul>
    }
}

export default TodoItems;

export class CheckboxComponent extends React.Component {
    render() {
        return (
            <li key={this.props.item.key}>
                <label text={this.props.item.text}>
                    <input type="checkbox" onClick={() => this.props.deleteItem(this.props.item.key)} />
                </label>
            </li>
        )
    }
}
